mapper.select:

	select *
	from
		chart_member
	where
		fitc_id = #{fitc_id}
		AND fitc_pw = #{fitc_pw}


	select *
	from
		chart_member
	where
		fitc_name = #{fitc_name}
		AND fitc_email = #{fitc_email}


	select *
	from
		chart_member
	where
		fitc_id = #{fitc_id}
		AND fitc_name = #{fitc_name}
		AND fitc_email = #{fitc_email}

mapper.update:
update chart_member set
fitc_pw = #{fitc_pw},
fitc_name = #{fitc_name}, fitc_email = #{fitc_email} where fitc_id = #{fitc_id}
