mapper.select:

	select *
	from
		chart_member
	where
		fitc_id = #{fitc_id}
		AND fitc_pw = #{fitc_pw}


	select *
	from
		chart_member
	where
		fitc_name = #{fitc_name}
		AND fitc_email = #{fitc_email}


	select *
	from
		chart_member
	where
		fitc_id = #{fitc_id}
		AND fitc_name = #{fitc_name}
		AND fitc_email = #{fitc_email}
